package conditional_statement;

public class swtich_statement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		switch("uttapa") {
		case "misal":{
			System.out.println(" breakfast on monday");
			break;
		}
		case"pohe":{
			System.out.println("breakfast on tresday");
			break ;
		}
		case "upma": {
			System.out.println("breakfast on wednsday");
			break ;
		}
		case "uttapa": {
			System.out.println("breakfast on thursday");
			break ;
		}
		case "idli": {
			System.out.println("breakfast on friday");
			break ;
		}
		case "dosa": {
			System.out.println("breakfast on saturday");
			break ;
		}
		default:{
			System.out.println("no breakfast on sunday");
			break ;
		}
		}

	}

}
